import React, { Component } from 'react'
import './inputForms.css'

class InputForm extends Component {
    
    constructor(props) {
        super(props);
        this.state = {   
            data: "",
        }
    }


    handleTextChange = (event) => {
        this.setState({
            data : event.target.value,
        })
        var smallLabels = ""
        if (this.props.smallLabel != undefined){
            smallLabels = this.props.smallLabel
        }
        this.props.parentCallback(this.props.label+smallLabels+"&#!::!#&"+event.target.value);
    }
    
    handleDefault = () => {
        this.setState({
            data : this.props.defaults,
        })
    }
    
    render(){

        if (this.props.defaults != undefined){
            this.handleDefault();
        }

        return(
            
            <div>
                <p className={this.props.work}>{this.props.label}</p>
                <input type="text" className={this.props.size} onChange={this.handleTextChange} value={this.state.data}></input>
            </div>
        
        );

    }
}

export default InputForm
